import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Loader from "./components/Loader";
import { round } from "./redux/helpers/Math";
import { N_getOrderValume } from "./redux/helpers/api_functions_new"
import {
  BUY_MARKET_PRICE,
  SELL_MARKET_PRICE,
  SET_ORDER_BOOK,
} from "./redux/constant";
export default function   OrderSystemTab(props) {
  const [activeTab, setActiveTab] = useState(0);
  const [order_volume_loading, setOrderVolumeLoading] = useState(true);
  const [sarr, setsarr] = useState([]);
  const [buy_order, setBuyOrder] = useState([]);
  const [sell_order, setSellOrder] = useState([]);

  const [barr, setbarr] = useState([]);
  const { buy_order_book, sell_order_book, order_book_loading } = useSelector(
    (state) => state.coinDBReducer
  );
  const dispatch = useDispatch();
  const coin = props?.match?.params?.id?.split("-");
  const coinname = coin[0] + coin[1];
  const { webData } = useSelector((state) => state.websiteDBReducer);

  function reverseArr(input) {
    let ret = new Array();
    for (var i = input.length - 1; i >= 0; i--) {
      let updatearr ={
        compare_currency:input[i].compare_currency,
        currency_type:input[i].currency_type,
        raw_price:round(input[i].raw_price),
        volume:round(input[i].volume)
      }
      if(round(updatearr.volume)>0)
      ret.push(input[i]);
    }
    return ret;
  }

  useEffect(() => {
    if (buy_order_book || sell_order_book) {
      // console.log("test: ", buy_order_book, sell_order_book);
      // Object.keys(buy_order_book).map((item) => {
      // console.log("inbyob: ", item, )
      if (buy_order_book[coinname]) {
        let tarr = reverseArr(
          buy_order_book[coinname].sort((a, b) => a.raw_price - b.raw_price)
        );
        setbarr(tarr);
      } else {
        setbarr();
      }
      // });

      // Object.keys(sell_order_book).map((item) => {
      if (sell_order_book[coinname]) {
        let t1arr = reverseArr(
          sell_order_book[coinname].sort((a, b) => b.raw_price - a.raw_price)
        );
        setsarr(t1arr);
      } else {
        setsarr();
      }
      // });
    }
  }, [buy_order_book, sell_order_book, coinname]);

  function getPercentage(cv, arr) {
    // console.log("perc arr: ", arr);
    let tv = 0;
    for (let i = 0; i < arr.length; i++) {
      tv += parseFloat(arr[i].volume);
    }
    return (Number(cv) * 100) / Number(tv);
  }

  useEffect(() => {
    dispatch({ type: SET_ORDER_BOOK, order_book_loading: false });
  }, []);
  // console.log("tarr: ", barr, sarr);

 useEffect(()=>{
  setOrderVolumeLoading(true)
  N_getOrderValume(coin[0], coin[1]).then((res)=>{
    setOrderVolumeLoading(false)
    if(res.status === 200) {
      setBuyOrder(res.buy_stak);
      setSellOrder(res.sell_stak);
    }
  });
 }, coin)
  return (
    <>
      <div
        className={`${webData.bg_color}`}
        style={{ backgroundColor: webData.bg_color_code }}
      >
        <div style={{ height: "33px" }}>
          <div
            className="bb-1 title-order d-flex p-0"
            style={{
              justifyContent: "space-between",
              background: "rgba(0,0,0,0.1)",
            }}
          >
            <div className="d-flex align-items-center px-3">ORDER BOOK</div>
            <nav>
              <div className="nav nav-tabs d-flex" id="nav-tab" role="tablist">
                <a
                  className={`nav-item nav-link  py-1  ${
                    activeTab === 0 ? "active" : ""
                  }`}
                  id="nav-home-tab"
                  data-toggle="tab"
                  role="tab"
                  aria-controls="nav-home"
                  aria-selected="true"
                  onClick={() => setActiveTab(0)}
                >
                  Market Depth
                </a>
                <a
                  className={`nav-item nav-link  py-1 ${
                    activeTab === 1 ? "active" : ""
                  }`}
                  id="nav-profile-tab"
                  data-toggle="tab"
                  onClick={() => 
                    setActiveTab(1)
                  }
                  role="tab"
                  aria-controls="nav-profile"
                  aria-selected="false"
                >
                  Order Volume
                </a>
              </div>
            </nav>
          </div>
        </div>

        {/* 1st Tab  */}
        <div
          className="tab-content "
          style={{
            height: "325px",
            overflow: "hidden",
          }}
        >
          <div
            className={`tab-pane fade ${activeTab == 0 ? "active show" : ""}`}
          >
            <div className="order-book-container">
              {order_book_loading ? (
                <Loader />
              ) : buy_order_book?.length != 0 || sell_order_book.length != 0 ? (
                <>
                  <table
                    className="order-book-table order-book-table--open order-book-table--left"
                    style={{ height: "fit-content" }}
                  >
                    <colgroup>
                      <col width="10%" />
                      <col width="40%" />
                      <col width="50%" />
                    </colgroup>
                    <thead>
                      <tr>
                        <th></th>
                        <th className="text-right text-uppercase">
                          <h6>Volume</h6>
                        </th>
                        <th className="text-right text-uppercase">
                          <h6>Buy Price</h6>
                        </th>
                      </tr>
                    </thead>
                    <tbody className="buy" style={{fontFamily:"monospace"}}>
                      {buy_order_book
                        ? barr?.map((d, index) => (
                            <tr
                              key={index}
                              onClick={() => {
                                dispatch({
                                  type: BUY_MARKET_PRICE,
                                  data: {
                                    marketprice: d.raw_price,
                                    marketvolume: d.volume,
                                    active:1
                                  },
                                });
                              }}
                            >
                              <td className="status-dot-cell">
                                <div></div>
                              </td>
                              <td className="text-success">{round(d.volume)}</td>
                              <td>{round(d.raw_price)}</td>
                              <div
                                className="filler"
                                style={{
                                  width: getPercentage(d.volume, barr) + "%",
                                }}
                              ></div>
                            </tr>
                          ))
                        : null}
                    </tbody>
                  </table>
                  <table
                    className="order-book-table order-book-table--open order-book-table--right"
                    style={{ height: "fit-content" }}
                  >
                    <colgroup>
                      <col width="50%" />
                      <col width="40%" />
                      <col width="10%" />
                    </colgroup>
                    <thead className="p-0 m-0">
                      <tr className="p-0 m-0">
                        <th className="text-uppercase">
                          <h6>Sell Price</h6>
                        </th>
                        <th className="text-uppercase">
                          <h6>Volume</h6>
                        </th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody className="sell " style={{fontFamily:"monospace"}}>
                      {sarr
                        ? sarr.map((d, index) => (
                            <tr
                              key={index}
                              onClick={() => {
                                dispatch({
                                  type: SELL_MARKET_PRICE,
                                  data: {
                                    marketprice: d.raw_price,
                                    marketvolume: d.volume,
                                    active:0
                                  },
                                });
                              }}
                            >
                              <td className="text-danger" >{round(d.raw_price)}</td>
                              <td>{round(d.volume)}</td>
                              <td className="status-dot-cell">
                                <div></div>
                              </td>
                              <div
                                className="filler"
                                style={{
                                  width: getPercentage(d.volume, sarr) + "%",
                                }}
                              ></div>
                            </tr>
                          ))
                        : null}
                    </tbody>
                  </table>
                </>
              ) : (
                <div
                  className="d-flex justify-content-center align-items-center"
                  style={{ flex: 1, height: "328px" }}
                >
                  <p className="mdfthemetxt">No record found</p>
                </div>
              )}
            </div>
          </div>

          {/* 2nd Tab */}
          <div
            className={`tab-pane fade ${activeTab == 1 ? "active show" : ""}`}
          >
            <div className="order-book-container">
              {order_volume_loading ? (
                <Loader />
              ) : buy_order?.length != 0 || sell_order.length != 0 ? (
                <>
                  <table
                    className="order-book-table order-book-table--open order-book-table--left"
                    style={{ height: "fit-content" }}
                  >
                    <colgroup>
                      <col width="10%" />
                      <col width="40%" />
                      <col width="50%" />
                    </colgroup>
                    <thead>
                      <tr>
                        <th></th>
                        <th className="text-right text-uppercase">
                          <h6>Volume</h6>
                        </th>
                        <th className="text-right text-uppercase">
                          <h6>Buy Price</h6>
                        </th>
                      </tr>
                    </thead>
                    <tbody className="buy " style={{fontFamily:"monospace"}}>
                      {buy_order
                        ? buy_order?.map((d, index) => (
                            <tr
                              key={index}
                            >
                              <td className="status-dot-cell">
                                <div></div>
                              </td>
                              <td className="text-success">{round(d.volume)}</td>
                              <td>{round(d._id)}</td>
                              <div
                                className="filler"
                                style={{
                                  width: getPercentage(d.volume, buy_order) + "%",
                                }}
                              ></div>
                            </tr>
                          ))
                        : null}
                    </tbody>
                  </table>
                  <table
                    className="order-book-table order-book-table--open order-book-table--right"
                    style={{ height: "fit-content" }}
                  >
                    <colgroup>
                      <col width="50%" />
                      <col width="40%" />
                      <col width="10%" />
                    </colgroup>
                    <thead >
                      <tr>
                        <th className="text-uppercase">
                          <h6>Sell Price</h6>
                        </th>
                        <th className="text-uppercase">
                          <h6>Volume</h6>
                        </th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody className="sell " style={{fontFamily:"monospace"}} >
                      {sell_order
                        ? sell_order?.map((d, index) => (
                            <tr 
                              key={index}
                            >
                              <td className="text-danger">{round(d._id)}</td>
                              <td>{round(d.volume)}</td>
                              <td className="status-dot-cell">
                                <div></div>
                              </td>
                              <div
                                className="filler"
                                style={{
                                  width: getPercentage(d.volume, sell_order) + "%",
                                }}
                              ></div>
                            </tr>
                          ))
                        : null}
                    </tbody>
                  </table>
                </>
              ) : (
                <div
                  className="d-flex justify-content-center align-items-center"
                  style={{ flex: 1, height: "328px" }}
                >
                  <p className="mdfthemetxt">No record found</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
