import axios from "axios";
import React, { useEffect, useState } from "react";
import ReactApexChart from "react-apexcharts";
import { useSelector } from "react-redux";
import "./candlestick.css";



function CandleStick() {

  const [stock, setStock] = useState([]);
  const [symbol, setSymbol] = useState("wrxinr");
  const [time, setTime] = useState("1m");
  const [VHL, setVHL] = useState([]);
  const [nor, setNor] = useState();
  let a = useSelector(state => state.coinDBReducer.symbolcreated);

  console.log(symbol);
  const data = [];
  const series = [{ data }];

  useEffect(() => {
    setSymbol(a);
  }, [a])

  useEffect(() => {

    const ws = new WebSocket("wss://stream.wazirx.com/stream");
    ws.addEventListener('open', () => {
      ws.send(JSON.stringify({ event: 'subscribe', streams: [`${symbol}@kline_${time}`] }));

    });

    ws.onmessage = (e) => {
      const res = JSON.parse(e.data);
      const d = res.data;
      console.log(d);
      setNor({ 'T': d.T + 19800000, 'o': d.o, 'c': d.c, 'l': d.l, 'h': d.h });
    };
  }, [])


  if (nor?.o !== undefined) {
    data.push({ x: nor.T, y: [nor.c, nor.h, nor.l, nor.o] });
  }


  var options = {
    legend: {
      show: true,
      floating: true,
    },
    chart: {

      toolbar: {
        show: true,
        offsetX: 1,
        offsetY: 0,
        tools: {
          download: false,
          selection: true,
          zoom: true,
          zoomin: true,
          zoomout: true,
          pan: true,
          reset: false | '<img src="/static/icons/reset.png" width="20">',
          customIcons: [],
        },
        autoSelected: "zoom",
      },
      type: "candlestick",
      height: 350,
    },
    xaxis: {
      type: "datetime",
      labels: {
        datetimeFormatter: {
          year: "yyyy",
          month: "MMM 'yy",
          day: "dd MMM",
          hour: "HH:mm",
        },
      },
    },
    tooltip: {
      enabled: false,
    },
    annotations: {
      yaxis: [
        {
          y: VHL.lastPrice,
          borderColor: "Black",
          label: {
            borderColor: "black",
            style: {
              color: "#fff",
              background: "black",
            },
            text: `${VHL.lastPrice}`,
          },
        },
      ],
    },

  };

  useEffect(() => {
    axios({
      method: "get",
      url: `https://api.wazirx.com/sapi/v1/klines?symbol=${symbol}&limit=150&interval=${time}`,
    }).then((res) => {
      setStock(res.data);
    });
  }, [time, symbol, stock]);

  useEffect(() => {
    axios({
      method: "get",
      url: `https://api.wazirx.com/sapi/v1/ticker/24hr?symbol=${symbol}`,
    }).then((res) => {
      if (res.data === null) {
        console.log("error handle");
      } else {
        setVHL(res.data);
      }
    });

  }, [symbol]);

  function calIndex() {
    for (let i = 0; i < stock.length; i++) {
      let time = parseInt(stock[i][0] * 1000 + 19800000);
      let high = parseFloat(stock[i][2]);
      let close = parseFloat(stock[i][1]);
      let open = parseFloat(stock[i][4]);
      let low = parseFloat(stock[i][3]);
      data.push({ x: time, y: [close, high, low, open] });
    }
  }
  calIndex();

  // function handling(e) {
  //   setInputData(e.target.value);
  // }
  // function filttering(d) {
  //   if (
  //     inputData === "" ||
  //     d.baseAsset.toLowerCase().includes(inputData.toLocaleLowerCase())
  //   ) {
  //     return d;
  //   }
  // }

  return (

    <div>
      <div style={{ display: "flex" }}>
        <div>
          <h3
            style={{
              textAlign: "center",
              color: "green",
              marginLeft: "1rem",
            }}
          >
            {symbol.toLocaleUpperCase()}
          </h3>
        </div>
        <div style={{ color: "black", marginLeft: "70%" }}>
          <h3>Last Price â‚¹{VHL.lastPrice}</h3>
        </div>
      </div>
      <hr />
      <div style={{ display: "flex", marginLeft: "1%", height: "10px" }}>
        <p id="list" onClick={() => setTime("1m")}>
          1M
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("5m")}
        >
          5M
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("15m")}
        >
          15M
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("30m")}
        >
          30M
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("1h")}
        >
          1H
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("4h")}
        >
          4H
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("1d")}
        >
          1D
        </p>
        <p
          id="list"
          style={{ marginLeft: "1%" }}
          onClick={() => setTime("1w")}
        >
          1W
        </p>
        <div>
          <div style={{ display: "flex", marginLeft: "10%" }}>
            <p>Volume &nbsp;</p>
            <p style={{ fontWeight: "bold" }}>{VHL.volume} &nbsp;</p>
            <p>&nbsp; High &nbsp;</p>
            <p style={{ fontWeight: "bold" }}>{VHL.highPrice} &nbsp;</p>
            <p>&nbsp; low &nbsp;</p>
            <p style={{ fontWeight: "bold" }}>{VHL.lowPrice} &nbsp;</p>
          </div>
        </div>
      </div>
      <hr />
      <ReactApexChart
        id="apex-chart"
        options={options}
        series={series}
        type="candlestick"
        height={"500px"}
        width={"100%"}
      />
    </div>
  );
}

export default CandleStick;
